    fetch(url)
      .then((resp) => resp.json())
      .then((respJson) => {
        //setLoading(true);
        //setData(respJson.data);

        console.log("RESPUESTA", JSON.stringify(respJson));
      })
      .catch((error) => {
        console.log(error);
        //setLoading(false); // En caso de error, asegÃºrate de cambiar loading a false
      });
  };

  useEffect(() => {
    fetchApi();
